#include <stdio.h>
#include <stdlib.h>

int characterReplacement(char* s, int k) {   
    int maximal = 0, value = 0, length = 0, replace_count = -1, i = 0, j = 0;
    char *alias = NULL, *alias2 = NULL;
    int *table = NULL;
    
    //init data
    table = (int *)calloc(k, sizeof(int));
    alias = s;
    
    //do
    for (i = 'A'; i <= 'Z'; i++) {
        length = 0;
        replace_count = 0;
        for (j = 0; alias[j] != '\0'; j++) {
            if (!(i - alias[j]))
                length++;
            else {
                replace_count++;
                
                if (replace_count <= k) {
                    table[replace_count-1] = j;
                    length++;
                }
                else if (k == 0) {
                        maximal = (length > maximal)? length:maximal;
                        length = 0;
                }
                else {
                    value = table[(replace_count-1)%k];
                    length = j - value;
                    maximal = (length > maximal)? length:maximal;
                    table[(replace_count-1)%k] = j;
                }
            }
        }
        
        maximal = (length > maximal)? length:maximal;
    }
    
    //de-init
    free(table); 
    return maximal;
}

int main(void)
{
        int ret = 0;

        ret = characterReplacement("", 2666);

        printf("ret = %d\n", ret);

        return ret;
}
